INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(9, 13, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-9', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành