INSERT INTO `university` (`id`, `en_title`, `vi_title`, `en_description`, `vi_description`, `url`) VALUES
(1, 'Koguryeo University', 'Trường Cao đẳng Koguryeo', 'Xây dựng một nền tảng giáo dục vững chắc cùng đội ngũ giáo viên tâm\n                                                huyết. Chúng tôi cố gắng mang đến cho bạn một tương lai tươi sáng và\n                                                chắc chắn nhất.', 'Được thành lập từ năm 1995 ở thành phố Naju, tỉnh Jeollanam, trường Cao đẳng Koguryeo đã xây dựng cho mình nền tảng giáo giáo dục vững chắc với đội ngũ giảng viên nhiệt tình và giàu kinh nghiệm, hệ thống giáo dục tân tiến và trang thiết bị hiện đại.', 'http://www.kgrc.ac.kr'),
(2, 'Busan University', 'Đại học Quốc gia Pusan', 'Một trong những lựa chọn tốt nhất của sinh viên việt nam.', 'Được thành lập vào tháng 5 năm 1946, trường Đại học Quốc gia Pusan là một trong những trung tâm đào tạo và nghiên cứu hàng đầu của Hàn Quốc và được xếp thứ hai trong hệ thống trường đại học quốc gia ở Hàn Quốc.', 'http://www.pusan.ac.kr'),
(3, 'Yonsei University', 'Đại học Yonsei', 'Một trong những trường đại học uy tín nhất tại Hàn Quốc.', 'Được thành lập vào năm 1885 tại thủ đô Seoul, Yonsei là một trong những trường đại học lâu đời nhất của Hàn Quốc. Trường được đánh giá là một trong ba trường đại học tốt nhất của nước này.', 'http://www.yonsei.ac.kr');
