INSERT INTO `faq` (`id`, `en_question`, `vi_question`, `en_answer`, `vi_answer`, `created_date`, `updated_date`) VALUES
(1, 'Morbi efficitur metus?', 'Morbi efficitur metus?', 'Answer 1 Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque faucibus enim vitae arcu aliquam imperdiet. Morbi efficitur metus a neque egestas pellentesque. Proin lectus tortor, cursus non pretium eu, sagittis in felis. Nam at elit vel enim interdum tincidunt. Curabitur dapibus dui a lectus auctor, ac cursus mi efficitur.\n                                    ', '<p class="p1"><span class="s1"><b><i>❝</i></b></span><span class="s2"><b><i>Biết thêm một ngoại ngữ, mở thêm một chân trời</i></b></span><span class="s1"><b><i>❞</i></b></span></p><p class="p2"><span class="s2"><i>-Ludwig Wittgenstein-</i></span></p><p class="p3"><span class="s2"><b></b></span><br></p><p class="p4"><span class="s2"><b>Tại sao bạn nên học tiếng Hàn?</b></span></p><ul>\r\n<li class="li5"><span class="s3"></span><span class="s2"><i>Thành thạo tiếng Hàn sẽ giúp bạn có thu nhập cao hơn</i></span></li>\r\n<li class="li5"><span class="s3"></span><span class="s2"><i>Rút ngắn thời gian du học và cơ hội xin học bổng</i></span></li>\r\n<li class="li5"><span class="s3"></span><span class="s2"><i>Cơ hội khám phá và trải nghiệm văn hóa Hàn Quốc</i></span></li>\r\n<li class="li5"><span class="s3"></span><span class="s2"><i>Tiền đề cho bạn tìm kiếm cơ hội nghề nghiệp tốt trong tương lai</i></span></li>\r\n</ul><p class="p6"><span class="s2"><i></i></span><br></p><p class="p7"><span class="s4"><i>Hình ảnh</i></span></p><p class="p8"><span class="s2"><i></i></span><br></p><p class="p9"><span class="s2"><b>TRUNG TÂM HÀN NGỮ ISSILOO</b></span></p><p class="p10"><span class="s2"><b></b></span><br></p><p class="p5"><span class="s2">Xuất phát từ<b> </b>thực tiễn cũng như nhu cầu học tiếng Hàn của sinh viên Việt Nam hiện nay, Trung tâm ISSILOO đã xây dựng chương trình học tiếng Hàn căn cứ trên cấp học, ngành học và mục đích học tập của học viên.&nbsp;</span></p><p class="p5"><span class="s2"><b>Chương trình đào tạo của Trung tâm </b></span><span class="s5"><b>bao gồm các lớp học:</b></span></p><p class="p11"><span class="s2">+ Tiếng Hàn Sơ cấp/ Trung cấp/ Cao cấp</span></p><p class="p11"><span class="s2">+ Tiếng Hàn giao tiếp</span></p><p class="p11"><span class="s2">+ Biên phiên dịch</span></p><p class="p11"><span class="s2">+ Luyện thi topik</span></p><p class="p11"><span class="s2">+ Lớp du học cấp tốc</span></p><p class="p11"><span class="s2">+ Lớp học 1 kèm 1&nbsp;</span></p><p class="p5"><span class="s2"><b>Chương trình học:</b></span></p><p class="p5"><span class="s2">Về nội dung học, Trung tâm tập trung xây dựng nôi dung đa dạng và phong phú, tuân theo giáo trình chuẩn. Bên cạnh đó, ISSILOO sẽ đưa ra phương pháp luyện tập trực quan, các hình minh họa bắt mắt, sinh động giúp tăng khả năng ghi nhớ của học viên.</span></p><p class="p5"><span class="s2">Chương trình giảng dạy bao gồm tất cả nội dung: từ vựng, ngữ pháp, nghe hiểu, đọc hiểu, giao tiếp… kèm với các bài nghe sinh động, thú vị.</span></p><p class="p5"><span class="s2"><b><i>Luyện nghe</i></b></span></p><p class="p5"><span class="s2">Các file audio chuẩn và lượng video phong phú, giúp cải thiện khả năng nghe tiếng Hàn một cách rõ rệt.</span></p><p class="p5"><span class="s2"><b><i>Đọc hiểu</i></b></span></p><p class="p5"><span class="s2">Giúp người học nâng cao khả năng phán đoán và kỹ năngcân đối thời gian khi đọc.</span></p><p class="p5"><span class="s2"><b><i>Ngữ pháp</i></b></span></p><p class="p5"><span class="s2">Đưa ra các bài tập giúp người học nắm vững những quy tắc ngữ pháp trong tiếng Hàn.</span></p><p class="p5"><span class="s2"><b><i>Từ vựng</i></b></span></p>', '2016-05-11 09:12:47', '2016-05-11 09:12:47'),
