INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(1, 21, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-1', 'Du học tại Hàn Quốc', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU)', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU)', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. ', 'Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. ');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(2, 21, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-2', 'Du học tại HQ 1', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 1', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 1', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(3, 21, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-3', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(4, 21, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-4', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(6, 12, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-6', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(8, 12, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-8', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(9, 13, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-9', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(10, 13, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-10', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(11, 13, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-11', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.'),
(12, NULL, NULL, ' test', ' test', ' teset', ' test', 'KR- University DONGGUK (GYEONGJU) 2', 'tsttes test estests', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p>ddsf</p><p><br></p>', '2016-04-26 08:55:06', NULL, '<p>Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...Nhập nội dung rút gọn...</p>', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(13, 12, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-13', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(14, 18, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-14', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(15, 18, 'assets/upload/images/news/abc.jpg', 'du-hoc-tai-han-quoc-15', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(16, 8, 'https://www.youtube.com/watch?v=pdaTtASh-AY', 'goc-chia-se-1', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(17, 8, 'assets/upload/images/news/1.jpg', 'goc-chia-se-2', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(18, 8, 'assets/upload/images/news/3.jpg', 'goc-chia-se-3', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'KR- ĐẠI HỌC DONGGUK (GYEONGJU) 2', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<p><strong>1. KK 1Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '2016-04-26 08:55:06', NULL, 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. ', 'Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.');
INSERT INTO `news` (`id`, `category_id`, `img_src`, `slug`, `title_header`, `description_header`, `keyword_header`, `en_title`, `vi_title`, `en_content`, `vi_content`, `created_date`, `updated_date`, `vi_summary`, `en_summary`) VALUES
(19, 7, 'assets/upload/images/news/abc.jpg', 'test-cap-nhat-tintuc', 'Du học tại HQ 2', 'Du học tại Hàn Quốc', 'Du học, hàn quốc, issiloo', 'KR- University DONGGUK (GYEONGJU) 2', 'Cập nhật bài viết tin tức 1', '<p><strong>1.Giới thiệu về Trường<img class=" wp-image-3117 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-1.jpg" alt="Du học Hàn Quốc đại học Dongguk" width="316" height="87" /></strong></p>\n<p style="text-align: justify;"><em><strong>Trường Đại học Dongguk</strong></em> là một trường đại học tư thục lớn tại Hàn Quốc. Trường có cơ sở đào tạo ở Seoul, thành phố Gyeongju, tỉnh bắc Gyeongsang và đặc biệt tại Los Angeles – Hoa Kỳ. Ngoài ra trường còn đang hoạt động hai bệnh viện Tây y và bốn bệnh viện Đông y trong đó bao gồm Y học Hàn Quốc truyền thống.</p>\n<table style="height: 104px;" width="361" align="center">\n<tbody>\n<tr>\n<td style="background-color: #dbd0b4;">\n<p style="text-align: center;"><strong>TRƯỜNG ĐẠI HỌC DONGGUK (GYEONGJU)</strong></p>\n<p style="text-align: justify;"><strong>Địa chỉ:</strong> Dongguk University Gyeongju Campus 123, Dongdae-ro, Gyeongju-si, Gyeongsangbuk-do, Korea 780-714 Tel. 82.54.770.2114.</p>\n<p><strong>Web:</strong> dongguk.ac.kr.</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;">Được thành lập vào năm 1906, trường đại học Dongguk không chỉ là một trong những trường đại học lâu đời nhất Hàn Quốc mà còn là một trong số ít các trường đại học có bản sắc riêng đó là tín ngưỡng Phật giáo không trộn lẫn. Trải qua thế kỉ 20 đầy biến động, sự tàn phá ác liệt của chiến tranh dường như càng tô đậm thêm bản sắc của trường. Con voi được chọn là linh vật của trường, đây là con vật tượng trưng cho trí tuệ và vận may và cũng bởi lẽ đây là con vật biểu trưng cho sự hiện đại, tiến bộ và năng động của sinh viên trường đại học Dongguk bây giờ và trong tương lai. <img class="size-medium wp-image-3118 aligncenter" src="http://duhocico.edu.vn/wp-content/uploads/2016/01/du-hoc-han-quoc-Dongguk-2-350x136.jpg" alt="du-hoc-han-quoc-Dongguk (2)" width="350" height="136" /></p>\n<p style="text-align: justify;"> Mục tiêu của trường đại học Dongguk là giúp sinh viên sau này có thể nắm giữ được những vị trí chủ chốt trong nền kinh tế toàn cầu. Trường đại học cung cấp cho sinh viên không chỉ kiến thức chuyên ngành chuyên sâu, tầm nhìn chiến lược, kỹ năng phân tích mà còn cả những kỹ năng mềm cần thiết để sinh viên có đủ hành trang cho bản thân vững tin bước vào cuộc sống. Chính vì thế mà trường đại học Dongguk thu hút đông đảo du học sinh trên toàn thế giới với nền giáo dục toàn diện. Để đáp ứng đầy đủ các nhu cầu của sinh viên và thu hút thêm nhiều sinh viên quốc tế trường đại học Dongguk không ngừng cải tiến cơ sơ vật chất hiện đại như: bảo tàng, trung tâm thể hình, phòng học ngoại ngữ, phòng học đa phương tiện, café internet, sân bóng rổ. Theo những khảo sát gần đây, trường đại học Dongguk cũng góp mặt vào danh sách 20 trường đại học tốt nhất Hàn Quốc.</p>\n<p><strong>*Chuyên ngành đào tạo:</strong></p>\n<ul>\n<li><strong>Nhập học thẳng </strong></li>\n</ul>\n<table width="672" align="center">\n<tbody>\n<tr>\n<td width="126"><strong>Đại học thành viên</strong></td>\n<td width="126"><strong>Lĩnh vực</strong></td>\n<td width="420"><strong>Khoa</strong></td>\n</tr>\n<tr>\n<td rowspan="3" width="126"><strong>Phật học và Văn hóa</strong></td>\n<td rowspan="2" width="126"><strong>Nhân học</strong></td>\n<td width="420">Nghiên cứu Phật giáo</td>\n</tr>\n<tr>\n<td width="420">Giáo dục và chăm sóc trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td rowspan="8" width="126"><strong>Nhân học</strong></td>\n<td rowspan="6" width="126"><strong>Nhân học</strong></td>\n<td width="420">Ngôn ngữ và văn học Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử Hàn Quốc</td>\n</tr>\n<tr>\n<td width="420">Lịch sử ngành nghệ thuật và khảo cổ học</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="420">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="126"><strong>Nghệ thuật</strong></td>\n<td width="420">Nghệ thuật</td>\n</tr>\n<tr>\n<td width="126"><strong>Thể dục</strong></td>\n<td width="420">Giáo dục thể chất</td>\n</tr>\n<tr>\n<td rowspan="9" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td rowspan="3" width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Nghiên cứu sản phẩm – hóa học</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu dược vi sinh (Medical Bioscience)</td>\n</tr>\n<tr>\n<td width="420">Ứng dụng thống kê</td>\n</tr>\n<tr>\n<td rowspan="5" width="126"><strong>Kỹ thuật</strong></td>\n<td width="420">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="420">Nghiên cứu năng lượng hạt nhân</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật cơ khí</td>\n</tr>\n<tr>\n<td width="420">Kỹ thuật an toàn</td>\n</tr>\n<tr>\n<td width="126"><strong>Khoa học tự nhiên</strong></td>\n<td width="420">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td rowspan="4" width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Vấn đề quần chúng và sự quản lý của cảnh sát</td>\n</tr>\n<tr>\n<td width="420">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td width="420">Kinh tế và thương mại toàn cầu</td>\n</tr>\n<tr>\n<td width="420">Quản lý khách sạn và du lịch</td>\n</tr>\n<tr>\n<td width="126"><strong>Quản lý</strong></td>\n<td width="126"><strong>Khoa học xã hội</strong></td>\n<td width="420">Quản lý kinh doanh</td>\n</tr>\n<tr>\n<td width="126"><strong>Đại học PARAMITA</strong></td>\n<td width="126"><strong>Nhân học</strong></td>\n<td width="420">Không chọn được chuyên ngành</td>\n</tr>\n</tbody>\n</table>\n<p style="text-align: justify;"><em>*Học sinh mới đăng ký vào tất cả khóa học và ngành học chính không lien quan đến Âm nhạc truyền thống Hàn Quốc, nghệ thuật, giáo dục thể chất bắt buộc phải tham gia khóa hướng nghiệp để tìm kiếm đam mê và sẽ đăng ký vào chuyên ngành khi sang năm hai.</em></p>\n<ul>\n<li><strong>Chuyển tiếp</strong><strong>:</strong></li>\n</ul>\n<table width="678" align="center">\n<tbody>\n<tr>\n<td width="120"><strong>Đại học thành viên</strong></td>\n<td width="119"><strong>Lĩnh vực</strong></td>\n<td width="439"><strong>Khoa (chuyên ngành)</strong></td>\n</tr>\n<tr>\n<td rowspan="4" width="120"><strong>Phật học và văn hóa</strong></td>\n<td rowspan="2" width="119"><strong>Nhân học</strong></td>\n<td width="439">Nghiên cứu Phật giáo và Seon</td>\n</tr>\n<tr>\n<td width="439">Nghiên cứu trẻ em trong Phật giáo</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Âm nhạc truyền thống Hàn Quốc</td>\n</tr>\n<tr>\n<td width="439">Vẽ, Thiết kế (formative design), Kiến trúc truyền thống Hàn Quốc *</td>\n</tr>\n<tr>\n<td rowspan="6" width="120"><strong>Nhân học</strong></td>\n<td rowspan="4" width="119"><strong>Nhân học</strong></td>\n<td width="439">Ngôn ngữ và văn học Hàn quốc, Văn học Trung Quốc, Lịch sử Hàn Quốc, Lịch sử nghệ thuật và khảo cổ học, Xu hướng văn hóa và nghệ thuật *</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Anh</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Nhật Bản</td>\n</tr>\n<tr>\n<td width="439">Ngôn ngữ và văn học Trung Quốc</td>\n</tr>\n<tr>\n<td width="119"><strong>Nghệ thuật</strong></td>\n<td width="439">Nghệ thuật vẽ, thiết kễ mẫu và hình ảnh</td>\n</tr>\n<tr>\n<td width="119"><strong>Thể dục</strong></td>\n<td width="439">Vận động viên thể thao</td>\n</tr>\n<tr>\n<td rowspan="5" width="120"><strong>Khoa học và công nghệ</strong></td>\n<td width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Khoa học cuộc sống và công nghệ sinh học</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học xã hội/Kỹ thuật</strong></td>\n<td width="439">Công nghệ mới – Hóa học , biểu diễn thống kê, Kỹ thuật đảm bảo an toàn</td>\n</tr>\n<tr>\n<td rowspan="2" width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Kỹ thuật thông tin điện tử</td>\n</tr>\n<tr>\n<td width="439">Kỹ thuật máy tính</td>\n</tr>\n<tr>\n<td width="119"><strong>Khoa học tự nhiên</strong></td>\n<td width="439">Thiết kế quy hoạch</td>\n</tr>\n<tr>\n<td width="120"><strong>Năng lượng – Môi trường</strong></td>\n<td width="119"><strong>Kỹ thuật</strong></td>\n<td width="439">Năng lượng hạt nhân – Kỹ thuật năng lượng</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Xã hội học</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Quản lý xã hội, chính trị, ngoại giao, công an*</td>\n</tr>\n<tr>\n<td width="439">Phúc lợi xã hội</td>\n</tr>\n<tr>\n<td rowspan="2" width="120"><strong>Quản lý du lịch</strong></td>\n<td rowspan="2" width="119"><strong>Khoa học xã hội</strong></td>\n<td width="439">Kinh tế, thương mại quốc tế, quản lý, kế toán, quản lý thông tin</td>\n</tr>\n<tr>\n<td width="439">Du lịch và giải trí, Du lịch và quy tắc quản lý, Quản lý thực phẩm và đồ uống</td>\n</tr>\n</tbody>\n</table>\n<p><em>Lưu ý: Chuyên ngành với dấu * không dành cho học sinh chuyển tiếp</em><strong><br />\n</strong></p>\n<p><strong>2.Học phí</strong></p>\n<ul>\n<li>Chương trình học tiếng Hàn: $2.000/ 1 học kỳ (20 tuần)</li>\n<li>Đại học: $3.000 &#8211; $4.000/ học kỳ (16 tuần)</li>\n<li>Cao học: $4.000 – 5.000 / học kỳ (16 tuần)</li>\n</ul>\n<p><strong>3.Kí túc xá</strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="121"><strong>Chương trình</strong></td>\n<td width="84"><strong>Loại</strong></td>\n<td width="264"><strong>Trang thiết bị</strong></td>\n<td width="191"><strong>Chi phí</strong></td>\n</tr>\n<tr>\n<td width="121"><strong>Học tiếng Hàn</strong></td>\n<td width="84"><strong>Phòng 3</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần (riêng), điện thoại bàn, phòng tắm, phòng vệ sinh (chung), điện nước, internet.</td>\n<td width="191">$ 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong>Đại học và Cao học</strong></td>\n<td width="84"><strong>Phòng đôi</strong></td>\n<td width="264">Giường, bàn học, ghế, tử sách, tủ áo quần, phòng tắm, phòng vệ sinh (riêng), điện thoại bàn</td>\n<td width="191">$ 1.000 &#8211; 1.300/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần</td>\n</tr>\n<tr>\n<td width="121"><strong> </strong></td>\n<td width="84"><strong>Phòng ba</strong></td>\n<td width="264">Điện, nước, Internet</p>\n<p>Giường, bàn học, ghế, tử sách, tủ áo quần (riềng)</td>\n<td width="191">$ 1.000/ học kỳ bao gồm 3 bữa ăn trong ngày ngoại trừ cuối tuần.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4.Chương trình tiếng Hàn</strong><strong> </strong></p>\n<table width="642" align="center">\n<tbody>\n<tr>\n<td width="298"><strong>Thời gian</strong></td>\n<td width="345">2 kỳ/ năm ; 1 học kỳ (20 tuần)</td>\n</tr>\n<tr>\n<td width="298"><strong>Phí ghi danh</strong></td>\n<td width="345"> 50 USD</td>\n</tr>\n<tr>\n<td width="298"><strong>Chương trình học</strong></td>\n<td width="345">Trình độ 1 (bắt đầu) – trình độ 6 (nâng cao)</td>\n</tr>\n<tr>\n<td width="298"><strong>Kỳ nhập học tháng</strong></td>\n<td width="345">3,6,9,12</td>\n</tr>\n<tr>\n<td width="298"><strong>Số lượng học viên</strong></td>\n<td width="345">15 học viên/lớp</td>\n</tr>\n<tr>\n<td width="298"><strong>Học phí</strong></td>\n<td width="345">Học phí:  khoảng 4000 USD/ năm</td>\n</tr>\n</tbody>\n</table>\n<p><strong><em> </em></strong></p>\n<p><strong>5. Chi phí phải nộp trước khi xuất cảnh: </strong><em>(tham khảo kỳ trước, số liệu này có thể thay đổi tuỳ từng kỳ nhập học).</em><strong><em> </em></strong></p>\n<table align="center">\n<tbody>\n<tr>\n<td width="241"><strong>CÁC KHOẢN TIỀN</strong></td>\n<td width="436"><strong>SỐ TIỀN (USD)</strong></td>\n</tr>\n<tr>\n<td width="241"><strong>Phí ghi danh</strong></td>\n<td width="436">50</td>\n</tr>\n<tr>\n<td width="241"><strong>Học phí( 1 năm )</strong></td>\n<td width="436">4000</td>\n</tr>\n<tr>\n<td width="241"><strong>Kí túc xá + ăn  ( 1 kỳ)</strong></td>\n<td width="436">1300 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Bảo hiểm y tế 1 năm</strong></td>\n<td width="436">200 USD</td>\n</tr>\n<tr>\n<td width="241"><strong>Tổng</strong></td>\n<td width="436"><strong>5550</strong></td>\n</tr>\n</tbody>\n</table>', '<div id="wrap-article-seo" style="padding: 10px; font-size: 14px; font-family: arial; color: #000000; text-align: justify;">\r\n<h2 style="font-size: 20px; color: #003466; text-align: center;"><strong><u style="text-decoration: none;"><i style="font-style: normal;"><a style="text-decoration: none; color: #003466;" href="http://www.oecc.vn/thuc-tap-tai-singapore.html" rel="dofollow" title="Internship Singapore - Thực tập hưởng lương tại Singapore 2015" target="_blank">Internship Singapore - Thực tập hưởng lương tại Singapore 2016</a></i></u></strong></h2>\r\n<br>\r\n<p>Singapore là đất nước văn minh, hiện đại, xanh – sạch – đẹp bậc nhất trên thế giới, thu hút hàng chục triệu du khách quốc tế đến du lịch hàng năm. Singapore - “Con rồng châu Á” với nền kinh tế phát triển cao, môi trường sống an toàn, phong cách sống năng động. Với sự đầu tư của những tập đoàn nước ngoài vào Singapore mang theo văn hóa làm việc quốc tế chuyên nghiệp, là môi trường lý tưởng cho các bạn trẻ học hỏi và nâng cao kỹ năng. Bạn muốn đặt chân đến Singapore để khám phá văn hóa – đất nước- con người, nâng cao ngoại ngữ, trải nghiệm và học hỏi tác phong trong môi trường làm việc quốc tế chuyên nghiệp?</p>\r\n<p><strong><u style="text-decoration: none;"><i style="font-style: normal;">Chương trình thực tập hưởng lương tại Singapore (Internship Singapore)</i></u></strong>: Mang đến cho bạn cơ hội thử sức mình, vừa thực tập vừa học hỏi, trải nghiệm cuộc sống tuyệt vời tại Singapore!</p>\r\n<br><center>\r\n<h5><img style="box-shadow: 7px 5px 5px #68696A;" src="http://www.oecc.vn/images/upload/website-1/post/Thuc-tap/thuc-tap-singapore/thuc-tap-tai-singapore-1.jpg" alt="Thực tập Singapore (Internship Singapore) - Bạn Trần Thị Phương Thảo - Vị trí: Management Trainee" title="Thực tập Singapore (Internship Singapore) - Bạn Trần Thị Phương Thảo - Vị trí: Management Trainee" width="600px"></h5>\r\n<h4 style="font-size: 11px; margin-top: 10px;"><cite style="font-style: normal;" title="Thực tập Singapore (Internship Singapore) - Bạn Trần Thị Phương Thảo - Vị trí: Management Trainee"><strong><u style="text-decoration: none;"><i style="font-style: normal;">Thực tập Singapore (Internship Singapore)</i></u></strong> - Bạn Trần Thị Phương Thảo - Vị trí: Management Trainee</cite></h4>\r\n</center><br>\r\n<p>Chương trình do Công ty tư vấn giáo dục và nghề nghiệp Overseas liên kết với các đối tác là các doanh nghiệp tại Singapore cùng phối hợp thực hiện. Các bạn sẽ được bố trí <strong style="font-weight: normal;"><u style="text-decoration: none;"><i style="font-style: normal;">thực tập trong thời gian 3 - 6 tháng (ngắn hạn) hoặc chương trình dài hạn 12 - 24 tháng tại Singapore</i></u></strong> với vị trí phù hợp với nhu cầu và nguyện vọng của bản thân.</p>\r\n<p>Công ty OECC xin giới thiệu đến các bạn <strong><u style="text-decoration: none;"><i style="font-style: normal;">những lợi ích khi tham gia chương trình thực tập hưởng lương tại Singapore</i></u></strong>:</p>\r\n<ul style="list-style: none; margin-left: 0px;">\r\n<li>- Được nhận mức lương thực tập 16 triệu – 44 triệu/ tháng.</li>\r\n<li>- Học hỏi kiến thức và kinh nghiệm từ môi trường làm việc quốc tế năng động trong các lĩnh vực đa dạng: Nhà hàng - Khách sạn, Bán sỉ - Bán lẻ, Hành chính văn phòng, CNTT, Thiết kế, Điện, Điện tử, Cơ khí ...</li>\r\n<li>- Môi trường sinh động để thực hành và nâng cao khả năng ngoại ngữ (tiếng Anh, tiếng Hoa) không tốn kém chi phí.</li>\r\n<li>- Sau khi hoàn thành chương trình thực tập, các bạn sẽ được cấp chứng nhận hoàn thành chương trình và có cơ hội ﻿giữ lại <u><a style="color: #26b5f0;" href="http://www.oecc.vn/viec-lam-tai-singapore.html" rel="dofollow" title="Làm việc tại Singapore" target="_blank">làm việc tại Singapore</a></u> với mức lương hấp dẫn.</li>\r\n</ul>\r\n<br><center>\r\n<h5><img style="box-shadow: 7px 5px 5px #68696A;" src="http://www.oecc.vn/images/upload/website-1/post/Thuc-tap/thuc-tap-singapore/thuc-tap-tai-singapore-2.png" alt="Thực tập Singapore (Internship Singapore) - Bạn Huỳnh Anh Thư - Vị trí: Assistant Manager" title="Thực tập Singapore (Internship Singapore) - Bạn Huỳnh Anh Thư - Vị trí: Assistant Manager" width="600px"></h5>\r\n<h4 style="font-size: 11px; margin-top: 10px;"><cite style="font-style: normal;" title="Thực tập Singapore (Internship Singapore) - Bạn Huỳnh Anh Thư - Vị trí: Assistant Manager"><strong><u style="text-decoration: none;"><i style="font-style: normal;">Thực tập Singapore (Internship Singapore)</i></u></strong> - Bạn Huỳnh Anh Thư - Vị trí: Assistant Manager</cite></h4>\r\n</center><br> <br>\r\n<h3 style="font-size: 16px; color: #003466; text-align: left;">1. Đối tượng tham gia <strong><u style="text-decoration: none;"><i style="font-style: normal;">chương trình thực tập Singapore</i></u></strong>:</h3>\r\n<ul style="list-style: none; margin-left: 0px;">\r\n<li>- Trình độ: Sinh viên các trường Trung cấp – Cao đẳng – Đại học đang theo học hoặc vừa ra trường.</li>\r\n<li>- Ngoại ngữ: Tiếng Anh hoặc Tiếng Hoa giao tiếp.</li>\r\n<li>- Độ tuổi: 18-40 tuổi.</li>\r\n<li>- Đủ sức khỏe theo quy định.</li>\r\n</ul>\r\n<br>\r\n<h3 style="font-size: 16px; color: #003466; text-align: left;">2. Các lĩnh vực của <strong><u style="text-decoration: none;"><i style="font-style: normal;">chương trình thực tập Singapore</i></u></strong>:</h3>\r\n<div style="margin-top: 20px;">\r\n<table style="600px; margin: 0px auto; margin-top: 10px;">\r\n<tbody>\r\n<tr>\r\n<td style="padding: 5px; height: 25px; width: 250px; line-height: 25px; b